import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const App = () => {
  const [file, setFile] = useState(null);
  const [resultImageUrl, setResultImageUrl] = useState(null);

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleUpload = async () => {
    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await fetch('http://localhost:5000/upload', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const result = await response.json();
      // Convert Windows backslashes to forward slashes and create a relative path
      const relativeImagePath = result.result_image_path.replace(/\\/g, '/').replace(/^.*?output/, '/output');
      setResultImageUrl(`http://localhost:5000${relativeImagePath}`);
    } catch (error) {
      console.error('Fetch error:', error);
    }
  };


  return (
    <div className="container mt-5">
      <div className="row">
        <div className="col-md-6 offset-md-3">
          <div className="card">
            <div className="card-body">
              <h5 className="card-title">Upload Image</h5>
              <input type="file" className="form-control mb-3" onChange={handleFileChange} />
              <button className="btn btn-primary" onClick={handleUpload}>
                Upload
              </button>
              {resultImageUrl && (
                <div className="mt-4">
                  <h2 className="card-title">Result Image Path:</h2>
                  <p>{resultImageUrl}</p>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
