import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const App = () => {
  const [file, setFile] = useState(null);
  const [uploadedImageUrl, setUploadedImageUrl] = useState(null);
  const [resultImageUrl, setResultImageUrl] = useState(null);

  const handleFileChange = (event) => {
    const uploadedFile = event.target.files[0];
    setFile(uploadedFile);

    // Display the uploaded image
    const uploadedImageUrl = URL.createObjectURL(uploadedFile);
    setUploadedImageUrl(uploadedImageUrl);
  };

  const handleUpload = async () => {
    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await fetch('http://localhost:5000/upload', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const result = await response.json();
      setResultImageUrl(`http://localhost:5000${result.result_image_path}`);
    } catch (error) {
      console.error('Fetch error:', error);
    }
  };

  return (
    <div className="container mt-5">
      <div className="row">
        <div className="col-md-6 offset-md-3">
          <div className="card">
            <div className="card-body">
              <h5 className="card-title">Upload Image</h5>
              <input type="file" className="form-control mb-3" onChange={handleFileChange} />
              <button className="btn btn-primary" onClick={handleUpload}>
                Upload
              </button>
              {uploadedImageUrl && (
                <div className="mt-4">
                  <h2 className="card-title">Uploaded Image:</h2>
                  <img src={uploadedImageUrl} alt="Uploaded" className="img-fluid" />
                </div>
              )}
              {resultImageUrl && (
                <div className="mt-4">
                  <h2 className="card-title">Result Image:</h2>
                  <img src={resultImageUrl} alt="Result" className="img-fluid" />
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
