const handleUpload = async () => {
  const formData = new FormData();
  formData.append('file', file);

  try {
    const response = await fetch('http://localhost:5000/upload', {
      method: 'POST',
      body: formData,
    });

    if (!response.ok) {
      throw new Error('Network response was not ok');
    }

    const result = await response.json();
    const result_image_path = result.result_image_url;

    // Update the URL construction using window.location
    const result_image_url = `http://localhost:5000/result/${result_image_path.split('/').pop()}`;

    // Set the URL of the resulted image to be displayed
    setResultImageUrl(result_image_url);
  } catch (error) {
    console.error('Fetch error:', error);
  }
};
